
--ADDING NEW AUX COLUMN TO HL5/HL6 FOR MARKETING ACTIVITY FOR BKP, BEFORE UPDATE SCRIPT
alter table "MKTG_PLANNING_TOOL"."HL5" add(AUX_MARKETING_ACTIVITY_ID BIGINT null);
alter table "MKTG_PLANNING_TOOL"."HL6" add(AUX_MARKETING_ACTIVITY_ID BIGINT null);

--
UPDATE "MKTG_PLANNING_TOOL"."HL5"
SET AUX_MARKETING_ACTIVITY_ID = MARKETING_ACTIVITY_ID
WHERE ENABLED=1 AND DELETED = 0;

UPDATE "MKTG_PLANNING_TOOL"."HL6"
SET AUX_MARKETING_ACTIVITY_ID = MARKETING_ACTIVITY_ID
WHERE ENABLED=1 AND DELETED = 0;

--HERE IS THE UPDATE SCRIPT FORE NEW VALUES FOR MARKETING ACTIVITY REFERENCE

insert into "MKTG_PLANNING_TOOL"."MARKETING_ACTIVITY"(NAME, DESCRIPTION, CREATED_USER_ID)
SELECT "MKTG_PLANNING_TOOL"."mktgplanningtool.db.functions::GET_CRM_ID"(BGY.BUDGET_YEAR, HL1.ACRONYM, hl2.ORGANIZATION_ACRONYM, HL3.ACRONYM, HL4.ACRONYM, B.ACRONYM, '') as NAME
,"MKTG_PLANNING_TOOL"."mktgplanningtool.db.functions::GET_CRM_ID"(BGY.BUDGET_YEAR, HL1.ACRONYM, hl2.ORGANIZATION_ACRONYM, HL3.ACRONYM, HL4.ACRONYM, B.ACRONYM, '') as DESCRIPTION
, 1
FROM HL5
INNER JOIN HL5 B ON HL5.MARKETING_ACTIVITY_ID = B.HL5_ID
INNER JOIN HL4 ON B.HL4_ID = HL4.HL4_ID
INNER JOIN HL3 ON HL4.HL3_ID = HL3.HL3_ID
INNER JOIN HL2 ON HL3.HL2_ID = HL2.HL2_ID
INNER JOIN HL1 ON HL2.HL1_ID = HL1.HL1_ID
INNER JOIN BUDGET_YEAR BGY ON HL1.BUDGET_YEAR_ID = BGY.BUDGET_YEAR_ID
where

HL5.ENABLED = 1 and HL5.DELETED = 0
AND
HL5.MARKETING_ACTIVITY_ID IS NOT NULL;


