CREATE PROCEDURE "MKTG_PLANNING_TOOL"."mktgplanningtool.db.procedures::INS_LEGACY_RECORDS" (
    IN in_level integer
)
	LANGUAGE SQLSCRIPT
	SQL SECURITY INVOKER
	DEFAULT SCHEMA "MKTG_PLANNING_TOOL"
	AS
BEGIN

va_records_by_level = select * from LEGACY_DICTIONARY
            WHERE LEVEL = in_level;

va_final_data_set = SELECT
CAMPAIGN_ID,
CAMPAIGN_DESCRIPTION,
(CASE WHEN in_level = 5 THEN
(SELECT HL4_ID FROM
        (select hl4.hl4_id,
    CONCAT('CRM-',
        CONCAT(
            CONCAT(
                CONCAT(hl2.ORGANIZATION_ACRONYM, SUBSTRING(TO_NVARCHAR(BUDGET_YEAR.BUDGET_YEAR), LENGTH(BUDGET_YEAR.BUDGET_YEAR) - 1, 2))
                , '-')
        ,hl4.ACRONYM)
    ) as PATH
    from hl1
    inner join hl2 on hl2.hl1_id = hl1.hl1_id
    inner join hl3 on hl3.hl2_id = hl2.hl2_id
    inner join hl4 on hl4.hl3_id = hl3.hl3_id
    INNER JOIN BUDGET_YEAR ON HL1.BUDGET_YEAR_ID = BUDGET_YEAR.BUDGET_YEAR_ID
    where hl2.enabled = 1 and hl2.deleted = 0)
    WHERE PATH = TRIM(PARENT))
     ELSE
     (SELECT HL5_LEGACY_ID FROM HL5_LEGACY
    WHERE PATH = TRIM(PARENT))
     END) AS PARENT_ID,
(select distinct objective_id from objective
where crm_key = CAST(OBJECTIVE_CODE AS INTEGER)) as OBJECTIVE_CODE,
(select distinct campaign_type_id from campaign_type
where crm_key = trim(TYPE_CODE)) AS TYPE_CODE,
(select distinct campaign_sub_type_id from campaign_sub_type
where crm_key = trim(SUB_TYPE_CODE)) AS SUB_TYPE_CODE,
(select distinct PRIORITY_id from PRIORITY
where crm_key = CAST(PRIORITY_CODE AS INTEGER)) AS PRIORITY_CODE,
(CASE WHEN PLANNED_START = '' THEN NULL ELSE TO_TIMESTAMP(PLANNED_START, 'YYYY-MM-DD HH24:MI:SS') END) AS PLANNED_START,
(CASE WHEN PLANNED_FINISH = '' THEN NULL ELSE TO_TIMESTAMP(PLANNED_FINISH, 'YYYY-MM-DD HH24:MI:SS') END) AS PLANNED_FINISH,
(CASE WHEN ACTUAL_START_DATE = '' THEN NULL ELSE TO_TIMESTAMP(ACTUAL_START_DATE, 'YYYY-MM-DD HH24:MI:SS') END) AS ACTUAL_START_DATE,
(CASE WHEN ACTUAL_FINISH_DATE = '' THEN NULL ELSE TO_TIMESTAMP(ACTUAL_FINISH_DATE, 'YYYY-MM-DD HH24:MI:SS') END) AS ACTUAL_FINISH_DATE,
EMPLOYEE_RESPONSIBLE,
3 AS CAMPAIGN_STATUS,
(select distinct COST_CENTER_id from COST_CENTER
where TRIM(COST_CENTER_CODE) = TRIM(COST_CENTER)) AS COST_CENTER,
(select distinct BUSINESS_OWNER_id from BUSINESS_OWNER
where crm_key = trim(BUSINESS_OWNER)) AS BUSINESS_OWNER,
(select distinct SALES_ORGANIZATION_id from SALE_ORGANIZATION
where crm_key = trim(MARKETING_ORGANIZATION)) AS MARKETING_ORGANIZATION,
(select distinct DISTRIBUTION_CHANNEL_id from DISTRIBUTION_CHANNEL
where crm_key = CAST(DISTRIBUTION_CHANNEL_KEY AS INTEGER)) AS DISTRIBUTION_CHANNEL_KEY,
(SELECT COALESCE(MARKETING_PROGRAM_ID, NULL) FROM MARKETING_PROGRAM
WHERE TRIM(NAME) = TRIM(MARKETING_PROGRAM_ID)) AS MARKETING_PROGRAM_ID,
(SELECT COALESCE(MARKETING_ACTIVITY_ID, NULL) FROM MARKETING_ACTIVITY
WHERE TRIM(NAME) = TRIM(MARKETING_ACTIVITY_ID)) AS MARKETING_ACTIVITY_ID,
(select distinct ROUTE_TO_MARKET_id from ROUTE_TO_MARKET
where crm_key = CAST(ROUTE_TO_MARKET AS INTEGER)) AS ROUTE_TO_MARKET,
(SELECT USER_ID FROM USER
WHERE USER_NAME = TRIM(CHANGED_BY)) AS CHANGED_BY,
(CASE WHEN CHANGED_ON = '' THEN NULL ELSE TO_TIMESTAMP(CHANGED_ON, 'YYYY-MM-DD HH24:MI:SS') END) AS CHANGED_ON,
(SELECT USER_ID FROM USER
WHERE USER_NAME = TRIM(CREATED_BY)) AS CREATED_BY,
(CASE WHEN CREATED_ON = '' THEN NULL ELSE TO_TIMESTAMP(CREATED_ON, 'YYYY-MM-DD HH24:MI:SS') END) AS CREATED_ON,
(CASE WHEN SHOW_ON_CALENDAR = 'X' THEN 1 ELSE 0 END) AS SHOW_ON_CALENDAR,
0 AS BUDGET,
1 AS IN_BUDGET,
0 AS BUDGET_SPEND_Q1,
0 AS BUDGET_SPEND_Q2,
0 AS BUDGET_SPEND_Q3,
0 AS BUDGET_SPEND_Q4,
(SELECT C.EURO_CONVERSION_ID FROM CURRENCY C
 INNER JOIN BUDGET_YEAR BY ON BY.BUDGET_YEAR_ID = C.BUDGET_YEAR_ID
 WHERE C.DEFAULT_CURRENCY = 1 AND BY.BUDGET_YEAR = 2018
 AND C.DELETED = 0 AND C.ENABLED = 1) AS EURO_CONVERSION_ID
FROM :va_records_by_level;

IF in_level = 5 THEN
    , ACTUAL_START_DATE, ACTUAL_END_DATE, PLANNED_START_DATE, PLANNED_END_DATE, EMPLOYEE_RESPONSIBLE_USER, HL5_STATUS_DETAIL_ID
    , COST_CENTER_ID, BUSINESS_OWNER_ID, SALES_ORGANIZATION_ID, DISTRIBUTION_CHANNEL_ID, MARKETING_PROGRAM_ID
    , MARKETING_ACTIVITY_ID, ROUTE_TO_MARKET_ID, MODIFIED_USER_ID, MODIFIED_DATE_TZ, CREATED_USER_ID, CREATED_DATE_TZ
    , SHOW_ON_DG_CALENDAR, BUDGET, IN_BUDGET, BUDGET_SPEND_Q1, BUDGET_SPEND_Q2, BUDGET_SPEND_Q3, BUDGET_SPEND_Q4, EURO_CONVERSION_ID)
    (SELECT CAMPAIGN_ID
    , CAMPAIGN_DESCRIPTION
    , PARENT_ID
    , OBJECTIVE_CODE
    , TYPE_CODE
    , SUB_TYPE_CODE
    , PRIORITY_CODE
    , PLANNED_START
    , PLANNED_FINISH
    , ACTUAL_START_DATE
    , ACTUAL_FINISH_DATE
    , EMPLOYEE_RESPONSIBLE
    , CAMPAIGN_STATUS
    , COST_CENTER
    , BUSINESS_OWNER
    , MARKETING_ORGANIZATION
    , DISTRIBUTION_CHANNEL_KEY
    , MARKETING_PROGRAM_ID
    , MARKETING_ACTIVITY_ID
    , ROUTE_TO_MARKET
    , CHANGED_BY
    , CHANGED_ON
    , CREATED_BY
    , CREATED_ON
    , SHOW_ON_CALENDAR
    , BUDGET
    , IN_BUDGET
    , BUDGET_SPEND_Q1
    , BUDGET_SPEND_Q2
    , BUDGET_SPEND_Q3
    , BUDGET_SPEND_Q4
    , EURO_CONVERSION_ID
    FROM :va_final_data_set);
ELSE
    , ACTUAL_START_DATE, ACTUAL_END_DATE, PLANNED_START_DATE, PLANNED_END_DATE, EMPLOYEE_RESPONSIBLE_USER, HL6_STATUS_DETAIL_ID
    , COST_CENTER_ID, BUSINESS_OWNER_ID, SALES_ORGANIZATION_ID, DISTRIBUTION_CHANNEL_ID, MARKETING_PROGRAM_ID
    , MARKETING_ACTIVITY_ID, ROUTE_TO_MARKET_ID, MODIFIED_USER_ID, MODIFIED_DATE_TZ, CREATED_USER_ID, CREATED_DATE_TZ
    , SHOW_ON_DG_CALENDAR, BUDGET, IN_BUDGET, BUDGET_SPEND_Q1, BUDGET_SPEND_Q2, BUDGET_SPEND_Q3, BUDGET_SPEND_Q4, EURO_CONVERSION_ID)
    (SELECT CAMPAIGN_ID
    , CAMPAIGN_DESCRIPTION
    , PARENT_ID
    , OBJECTIVE_CODE
    , TYPE_CODE
    , SUB_TYPE_CODE
    , PRIORITY_CODE
    , PLANNED_START
    , PLANNED_FINISH
    , ACTUAL_START_DATE
    , ACTUAL_FINISH_DATE
    , EMPLOYEE_RESPONSIBLE
    , CAMPAIGN_STATUS
    , COST_CENTER
    , BUSINESS_OWNER
    , MARKETING_ORGANIZATION
    , DISTRIBUTION_CHANNEL_KEY
    , MARKETING_PROGRAM_ID
    , MARKETING_ACTIVITY_ID
    , ROUTE_TO_MARKET
    , CHANGED_BY
    , CHANGED_ON
    , CREATED_BY
    , CREATED_ON
    , SHOW_ON_CALENDAR
    , BUDGET
    , IN_BUDGET
    , BUDGET_SPEND_Q1
    , BUDGET_SPEND_Q2
    , BUDGET_SPEND_Q3
    , BUDGET_SPEND_Q4
    , EURO_CONVERSION_ID
    FROM :va_final_data_set);
END IF;

END;

CALL "MKTG_PLANNING_TOOL"."mktgplanningtool.db.procedures::INS_LEGACY_RECORDS" (5);
CALL "MKTG_PLANNING_TOOL"."mktgplanningtool.db.procedures::INS_LEGACY_RECORDS" (6);
