CREATE COLUMN TABLE "MKTG_PLANNING_TOOL"."USER_NAVIGATION" (
    "USER_NAVIGATION_ID" BIGINT NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "USER_ID" BIGINT NOT NULL,
    "ROUTE_NAME" NVARCHAR(255),
    "BUDGET_YEAR_ID" BIGINT,
    "REGION_ID" BIGINT,
    "SEARCH_STRING" NVARCHAR(255),
    "SELECTED_ITEM_ID" BIGINT,

    "CREATED_DATE_TZ" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "MODIFIED_DATE_TZ" TIMESTAMP,
    "CREATED_USER_ID" BIGINT NOT NULL,
    "MODIFIED_USER_ID" BIGINT,
    "ENABLED" TINYINT DEFAULT 1,
    "DELETED" TINYINT DEFAULT 0,

    FOREIGN KEY ("CREATED_USER_ID") REFERENCES "MKTG_PLANNING_TOOL"."USER"("USER_ID"),
    FOREIGN KEY ("USER_ID") REFERENCES "MKTG_PLANNING_TOOL"."USER"("USER_ID")
);


